#!usr/bin/env python3

#find ecoRI

dna = ''

#dna = 'AAAGAATTCAAAGAATTCAAA'

dna2 = dna

# for loop to find all EcoRI sites in dna sequence
start = 0
loc = 0
location = []
for i in range(len(dna)):
    loc = dna.find('GAATTC', start)
    print(f'loc: {loc}')
    if loc == -1:
        break
    elif loc not in location:
        location.append(loc)
        start += loc + 1
print(location)

# add cut sign at cut sights
for loc in location:
    dna2 = (dna2[:(loc+1)]) + '^' + dna2[(loc+2):]

#perform the cuts
cuts = dna2.split('^')

#find what site each fragment started at
lens = []
frags = {}
for i in cuts:
    print(f'first site: {dna.find(i)+1}, end site: {dna.find(i) + 1 + len(i)}, frag length: {len(i)}')
    frags[len(i)] = i
    lens.append(len(i))

# sort fragments by length + print
s_lens = sorted(lens, reverse=True)
print(s_lens)
for i in s_lens:
    print(frags[i], i)