# it matters how you initialize the set!!

s1 = {3, 14, 15, 9, 26, 5, 35, 9}
s2 = {60, 22, 14, 0, 9}

# intersection:
print(f'intersection: {s1 & s2}')
# difference
print(f'difference / s1 only: {s1-s2}')
print(f'difference / s2 only: {s2-s1}')
# union
print(f'union: {s1 | s2}')
# symetrical difference
print(f'symetrical difference: {s1 ^ s2}')

# dna sequence set
dna1 = set('')
dna2 = {''}

# problem 4

dna = ''

# unique characters:
dna_set = set(dna)
print(dna_set)

# count number of each unique character
dna_dict = {}
for nt in dna:
	if nt not in dna_dict:
		dna_dict[nt] = 1
	else:
		dna_dict[nt] += 1
print(dna_dict)

# calculate each nucleotide composition
a_comp = dna_dict['A'] / len(dna)
t_comp = dna_dict['T'] / len(dna)
c_comp = dna_dict['C'] / len(dna)
g_comp = dna_dict['G'] / len(dna)

print(f'A comp: {a_comp:.2%}, T comp {t_comp:.2%}, C comp: {c_comp:.2%}, G comp: {g_comp:.2%}')

# calculate GC content
print(f'GC content: {c_comp+g_comp:.2%}')
